import React, { useEffect, useRef, useState } from "react";

const SeatingChart = ({ checkedInTable }) => {
  const canvasRef = useRef(null);
  const tables = 30;
  const chairsPerTable = 8;
  const canvasWidth = 800;
  const canvasHeight = 500;
  const tableRadius = 30;
  const chairRadius = 10;
  const tableSpacing = 120;
  const rows = 5;
  const cols = 6;
  const [highlightedChair, setHighlightedChair] = useState(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    for (let i = 0; i < tables; i++) {
      const row = Math.floor(i / cols);
      const col = i % cols;
      const x = col * tableSpacing + tableSpacing / 2;
      const y = row * tableSpacing + tableSpacing / 2;

      // Draw table
      ctx.beginPath();
      ctx.arc(x, y, tableRadius, 0, Math.PI * 2);
      ctx.fillStyle = "#3498db";
      ctx.fill();
      ctx.stroke();
      ctx.fillStyle = "white";
      ctx.textAlign = "center";
      ctx.fillText(i + 1, x, y + 5);

      // Draw chairs around the table
      for (let j = 0; j < chairsPerTable; j++) {
        const chairAngle = (j / chairsPerTable) * Math.PI * 2;
        const chairX = x + Math.cos(chairAngle) * (tableRadius + 20);
        const chairY = y + Math.sin(chairAngle) * (tableRadius + 20);

        ctx.beginPath();
        ctx.arc(chairX, chairY, chairRadius, 0, Math.PI * 2);
        ctx.fillStyle = highlightedChair?.table === i + 1 && highlightedChair?.chair === j ? "#e74c3c" : "#2ecc71";
        ctx.fill();
        ctx.stroke();
      }
    }
  }, [highlightedChair]);

  useEffect(() => {
    if (checkedInTable) {
      const randomChair = Math.floor(Math.random() * chairsPerTable);
      setHighlightedChair({ table: checkedInTable, chair: randomChair });
    }
  }, [checkedInTable]);

  return <canvas ref={canvasRef} width={canvasWidth} height={canvasHeight} style={{ border: "1px solid black" }} />;
};

export default SeatingChart;
